import React, { Component } from 'react';

class SignInPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
        username: '',
        email: '',
        passwordOne: '',
        passwordTwo: '',
        error: null
    }
  }

  onSubmit = event => {
  }

  onChange = event => {
    this.setState({ [event.target.name]: event.target.value });
  };

  render() {
    return (
      <div style = {styles.formContainer}>
        <form style = {styles.formStyle} onSubmit={this.onSubmit}>
            <input
                style={styles.inputStyle}
                name="username"
                onChange={this.onChange}
                type="text"
                placeholder="Full Name"
            />
            <input
                style={styles.inputStyle}
                name="email"
                onChange={this.onChange}
                type="text"
                placeholder="Email Address"
            />
            <input
                style={styles.inputStyle}
                name="passwordOne"
                onChange={this.onChange}
                type="password"
                placeholder="Password"
            />
            <input
                style={styles.inputStyle}
                name="passwordTwo"
                onChange={this.onChange}
                type="password"
                placeholder="Confirm Password"
            />
            <button 
                type="submit"
                style={styles.buttonStyle}>
                SIGN UP
            </button>
        </form>
      </div>
    );
  }
}

const styles = {
    formContainer:{
        padding: 20,
        width: '100%',
        height: '90vh',
        borderRadius: 10,
        backgroundColor: '#FAFAFA',
        display: 'flex',
        justifyContent: 'center'
    },
    formStyle: {
        display: 'flex',
        width: '60%',
        flexDirection: 'column'
    },
    inputStyle: {
        fontFamily: 'merriweather',
        padding: 20,
        margin: 5,
        borderRadius: 10,
        border: 'none',
        boxShadow: '-1px 0 5px 0 rgba(0, 0, 0, .1)'
    },
    buttonStyle: {
        backgroundColor: '#C76161',
        color: 'white',
        marginTop: 10,
        fontWeight: 'bold',
        height: 50,
        borderRadius: 10,
        border: 'none',
        boxShadow: '-1px 0 5px 0 rgba(0, 0, 0, .1)'
    }
}

export default SignInPage;
